INSERT INTO `t_prokum` VALUES (117, 1, 1, b'1', '12', 'PENYERTAAN MODAL PEMERINTAH KOTA MOJOKERTO PADA PERUSAHAAN DAERAH AIR MINUM MAJA TIRTA, DAN PERSEROAN TERBATAS BANK PEMBIAYAAN RAKYAT SYARIAH KOTA MOJOKERTO \r\n', 2013, 12, 'A', 'Perda No. 12 Tahun 2013', '\"<div>\r\nbahwa perusahaan daerah Kota Mojokerto diharapkan dapat\r\n</div>\r\n<div>\r\nbersaing dan berkembang sesuai dengan perkembangan\r\n</div>\r\n<div>\r\nekonomi daerah sekaligus dapat meningkatkan pendapatan\r\n</div>\r\n<div>\r\nasli daerah untuk menunjang pelaksana', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1970-01-01', '1970-01-01', '-', '-', '85498012 2013 ocr.pdf', '-', '-', '', b'0', b'0', '2019-11-16 12:06:11', 2, NULL, NULL);
