INSERT INTO `t_prokum` VALUES (834, 1, 1, b'1', '2', 'PENYELENGGARAAN LALU LINTAS DAN ANGKUTAN JALAN\r\n', 2013, 1, 'A', 'Perda No. 2 Tahun 2013', 'Bahwa dalam rangka menunjang perkembangan dan pertumbuhan perekonomian di Kata Mojokerto, diperlukan sebuah sistem lalu lintas dan angkutan jalan yang baik dan berdaya guna tinggi bahwa sistem lalu lintas dan angkutan jalan perlu diselenggarakan dengan mengintegrasikan seluruh komponen yang berkaitan, dalam sebuah kebijakan Pemerintah Daerah Kata Mojokerto berdasarkan kewenangan sesuai dengan peraturan perundang-undangan yang berlaku bahwa untuk mencapai maksud pada huruf a dan huruf b, maka perlu dibentuk sebuah Peraturan Daerah Kota Mojokerto tentang Lalu Lintas dan Angkutan Jalan.', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '2013-12-11', '2013-12-11', '-', '-', '9854432 2013 ocr.pdf', '-', '-', '', b'0', b'0', '2020-02-01 10:40:45', 2, '2020-02-01 10:40:45', 2);
INSERT INTO `t_prokum` VALUES (835, 1, 1, b'1', '3', 'PENYELENGGARAAN KETERTIBAN UMUM ', 2013, 2, 'A', 'Perda No. 3 Tahun 2013', 'Bahwa dalam rangka mewujudkan suatu kota yang indah, bersih, sehat tertib, aman tenteram, nyaman dan teratur merupakan hakikat dan perwujudan cita-cita ideal menuju keharmonisan dan keselarasan hidup dalam bingkai bermasyarakat sesuai dengan visi Kota Mojokerto, perlu mengatur tentang penyelengaraan ketertiban urnum Bahwa penyelenggaraan ketertiban umum di daerah merupakan salah satu urusan dalam skala Kabupaten/Kota yang memiliki peran strategis dalam rangka mewujudkan stabilitas sosial guna menunjang efektifitas pembangunan didaerah Bahwa berdasarkan pertimbangan sebagaimana dimaksud dalam huruf a dan huruf b maka perlu dibentuk Peraturan Daerah yang mengatur tentang Penyelengaraan Ketertiban Umum dan pedagang kaki lima.', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '2013-12-11', '2013-12-11', '-', '-', '3764643 2013 ocr.pdf', '-', '-', '', b'0', b'0', '2020-02-01 10:46:33', 2, NULL, NULL);
