INSERT INTO `t_prokum` VALUES (858, 2, 1, b'1', '22', 'PERUBAHAN ATAS PERATURAN WALIKOTA MOJOKERTO NOMOR 99 TAHUN 2019 TENTANG PENJABARAN ANGGARAN PENDAPATAN DAN BELANJA DAERAH TAHUN ANGGARAN 2020', 2020, 70, 'A', 'PERATURAN WALIKOTA MOJOKERTO NOMOR 22 TAHUN 2020 ', 'bahwa dalam rangka menampung pendapatan yang diterima\r\ndan belanja yang harus dianggarkan setelah Anggaran\r\nPendapatan dan Belanja Daerah (APBD) ditetapkan serta\r\nuntuk melaksanakan Peraturan Gubernur Jawa Timur\r\nNomor 91 Tahun 2019 tentang Perubahan Atas Peraturan\r\nGubemur Jawa Timur Nomor 75 Tahun 2019 tentang Alokasi\r\nDana Bagi Hasil Cukai Tembakau Kepada Provinsi dan\r\nKabupaten/Kota di Jawa Timur Tahun Anggaran 2020, dan\r\nBantuan Keuangan Khusus Kepada Pemerintah Kota\r\nMojokerto, serta dalam Rangka Penanggulangan Corona\r\nVirus Disease 2019 (Covid-19), maka perlu untuk melakukan\r\nperubahan terhadap Peraturan Walikota Mojokerto Nomor 99\r\nTahun 2019 tentang Penjabaran Anggaran Pendapatan dan\r\nBelanja Daerah Tahun Anggaran 2020', '', '', 'Mengubah', 'bahwa dalam rangka menampung pendapatan yang diterima dan belanja yang harus dianggarkan setelah Anggaran Pendapatan dan Belanja Daerah (APBD) ditetapkan serta untuk melaksanakan Peraturan Gubernur Jawa Timur Nomor 91 Tahun 2019 tentang Perubahan Atas Per', '2020-03-26', '2020-03-26', '', '84/A', 'PROKUM_DAERAH/PERWALI-2020-22-Perubahan_Atas_Perwali_PAPBD_TA_2019_opt.pdf', '', '', '', b'1', b'0', '2020-06-17 15:24:54', 1, '2020-06-17 15:24:54', 1);
