INSERT INTO `t_prokum` VALUES (869, 2, 1, b'1', '39', 'PERUBAHAN KEEMPAT ATAS PERATURAN WALIKOTA MOJOKERTO NOMOR 99\r\nTAHUN 2019 TENTANG PENJABARAN ANGGARAN PENDAPATAN DAN BELANJA\r\nDAERAH TAHUN ANGGARAN 2020', 2020, 11, 'A', 'PERWALI NO.39 TAHUN 2020', 'bahwa dalam rangka menindaklanjuti Keputusan\r\nBersama Menteri dalam Negeri dan Menteri Keuangan\r\nNomor : 119/2813/SJ, Nomor : 177/KMK.7/2020\r\nTentang Percepatan Penyesuaian Anggaran\r\nPendapatan Dan Belanja Daerah Tahun 2020 Dalam\r\nRangka Penanganan Corona Virus Disease 2019\r\n(COVID-19), serta Pengamanan Daya Beli Masyarakat\r\ndan Perekonomian Nasional, maka perlu\r\nmelaksanakan Penyesuaian pendapatan Transfer ke\r\nDaerah dan Dana Desa berdasarkan rincian alokasi\r\nTransfer ke Daerah dan Dana Desa yang ditetapkan\r\ndalam Peraturan Menteri Keuangan dan Penyesuaian\r\nPendapatan Asli Daerah dengan memperhitungkan\r\npotensi pajak daerah dan retribusi daerah serta\r\nmemperhatikan perkiraan asumsi makro, seperti\r\npertumbuhan rasio perpajakan daerah, pertumbuhan\r\nekonomi, dan tingkat inflasi tahun 2020 yang dapat\r\nmempengaruhi target pendapatan pajak daerah dan\r\nretribusi daerah sebagai akibat dari menurunnya\r\nkegiatan perekonomian dengan melakukan perubahan\r\nterhadap Peraturan Walikota Mojokerto Nomor 99\r\nTahun 2019 tentang Penjabaran Anggaran\r\nPendapatan dan Belanja Daerah Tahun Anggaran\r\n2020;', '', '', 'Mengubah', 'PERUBAHAN', '2020-05-28', '2020-05-28', '', '103/A', 'PROKUM_DAERAH/PERWALI-2020-39-perubahan_KEEMPAT__perwali_99_tahun_2019_tentang_Penjabaran_APBD_TA_2020_(autentikasi).pdf', '', '', '', b'1', b'0', '2020-07-14 10:17:28', 1, NULL, NULL);
INSERT INTO `t_prokum` VALUES (870, 2, 1, b'1', '48', 'PENETAPAN KATEGORI PENGELOMPOKAN KEMAMPUAN KEUANGAN DAERAH\r\nSERTA PELAKSANAAN DAN PERTANGGUNGJAWABAN DANA OPERASIONAL \r\nTAHUN ANGGARAN 2020\r\n ', 2020, 10, 'D', 'PERWALI NO.48 TAHUN 2020', 'bahwa sebagai tindak lanjut ketentuan Pasal 9 Peraturan\r\nMenteri Dalam Negeri Nomor 62 Tahun 2017 tentang\r\nPengelompokan Kemampuan Keuangan Daerah serta\r\nPelaksanaan dan Pertanggungjawaban Dana Operasional,\r\nmaka perlu adanya penetapan Kelompok Kemampuan\r\nKeuangan Daerah atas besaran Tunjangan Komunikasi\r\nIntensif dan Tunjangan Reses bagi Pimpinan Dewan\r\nPerwakilan Rakyat Daerah (DPRD) dan Anggota DPRD serta\r\nDana Operasional Pimpinan DPRD; ', '', '', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '2020-06-22', '2020-06-22', '', '108/D', 'PROKUM_DAERAH/PERWALI-2020-48-_Penetapan_Kategori_Pengelompokan_Kemampuan_Keuangan_Daerah_serta_Pelaksanaan_dan_Pertanggungjawaban_Dana_Operasional_(autentikasi).pdf', '', '', '', b'1', b'0', '2020-07-14 10:37:02', 1, NULL, NULL);
