INSERT INTO `t_prokum` VALUES (875, 2, 1, b'1', '1', ' PERUBAHAN KEDUA ATAS PERATURAN WALIKOTA MOJOKERTO NOMOR 115 TAHUN 2014 TENTANG PERJALANAN DINAS DALAM NEGERI DI LINGKUNGAN PEMERINTAH KOTA MOJOKERTO\r\n', 2020, 9, 'A', 'PERWALI NO. 1 TAHUN 2020', 'bahwa atas hasil evaluasi pelaksanaan pengelolaan keuangan daerah\r\nkhususnya terkait pemberian biaya perjalanan dinas dalam negeri bagi \r\nsuami/istri Kepala Daerah dan Wakil Kepala Daerah di lingkungan\r\nPemerintah Kota Mojokerto, maka perlu dilakukan perubahan atas Peraturan \r\nWalikota Mojokerto Nomor 115 Tahun 2014 tentang Perjalanan Dinas Dalam\r\nNegeri di Lingkungan Pemerintah Kota Mojokerto dan menetapkan ketentuan\r\npengubahannya dalam Peraturan Walikota Mojokerto. ', '', '', 'Mengubah', 'MENGUBAH', '2019-01-02', '2019-01-02', '', '1', 'PROKUM_DAERAH/PERWALI-2019-1-Perubahan_kedua_Atas_Perwali_Nomor_115_tah_2014_tentang_SPPD.pdf', '', '', '', b'1', b'0', '2020-07-15 10:04:01', 1, NULL, NULL);
INSERT INTO `t_prokum` VALUES (876, 2, 1, b'1', '2', 'PERUBAHAN ATAS PERATURAN WALIKOTA MOJOKERTO NOMOR 47 TAHUN 2018\r\nTENTANG ANALISA STANDAR BELANJA (ASB) PEMERINTAH KOTA MOJOKERTO', 2019, 6, 'E', 'PERWALI NO.2 TAHUN 2019', 'bahwa dalam rangka optimalisasi pelaksanaan tugas Walikota, Wakil\r\nWalikota, Dewan Perwakilan Rakyat Daerah (DPRD) Kota\r\nMojokerto,dan Sekretaris Daerah Kota Mojokerto serta Kader\r\nMotivator di lingkungan Pemerintah Kota Mojokerto, maka perlu\r\ndilakukan Perubahan Analisa Standar Belanja khususnya terkait\r\nstandarisasi harga atau biaya perjalanan dinas dalam negeri bagi\r\nKepala Daerah dan Wakil Kepala Daerah serta Dewan Perwakilan\r\nRakyat Daerah (DPRD) di Lingkungan Pemerintah Kota Mojokerto,\r\nsebagaimana telah ditetapkan dalam Peraturan Walikota Mojokerto\r\nNomor 47 Tahun 2018 tentang Analisa Standar Belanja (ASB) Kota\r\nMojokerto yang dituangkan dalam Peraturan Walikota Mojokerto', '', '', 'Mengubah', 'MENGUBAH', '2019-01-02', '2019-01-02', '', '2/E', 'PROKUM_DAERAH/PERWALI-2019-2-Perubahan_Atas_Peraturan_Walikota_Mojokerto_Nomor_47_Tahun_20-18_Tentang_ASB.pdf', '', '', '', b'1', b'0', '2020-07-15 14:05:55', 1, NULL, NULL);
