INSERT INTO `t_prokum` VALUES (951, 1, 1, b'1', '15', 'RETRIBUSI PENGGUNAAN MESIN GILAS JALAN YANG DIKUASAI OLEH PEMERINTAH KOTAMADYA DAERAH TINGKAT II MOJOKERTO', 1977, 3, 'C', 'Perda No. 15 tahun 1977', 'Bahwa Mesin Gilas jalan dalam hal tidak dipergunakan untuk dinas, maka dapat dimanfaatkan guna melayani kepentingan umum dan di samping itu dalam usaha penggalian sumber - sumber pendapatan Daerah, maka perlu di atur tentang penggunaan dan retribusi mesin gilas yang dikuasai oleh pemerintah kotamadya daerah tingkat II Mojokerto oleh pihak lain ', 'UU no. 5 Tahun 1974', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1978-09-23', '1978-11-06', '-', '-', 'PROKUM_DAERAH/Perda_No__15_th_1977.pdf', '', '', '', b'1', b'0', '2020-07-23 13:01:51', 1, '2020-07-23 13:01:51', 1);
INSERT INTO `t_prokum` VALUES (952, 1, 1, b'1', '16', 'PERUBAHAN KETUJUH KALI PERATURAN DAERAH KOTAPRAJA MOJOKERTO NO. 6 TAHUN 1961 TENTANG MENGATUR RETRIBUSI UNTUK MENGGUNAKAN HALAMAN DAN BANGUNAN-BANGUNAN DI DALAM PASAR YANG DI KUASAI OLEH PEMERINTAH DAERAH KOTAPRAJA MOJOKERTO TANGGAL 27 APRIL 1961', 1977, 3, 'C', 'Perda No. 16 tahun 1977', 'Berhubung Tarif Retribusi pasar yang tercantum dalam pasal 6 ayat (1) dan ketentuan ancaman hukuman sebagaimana ditetapkan dalam pasal 18 peraturan daerah kotapraja mojokerto nomor 6 tahun 1961 sebagaimana telah diubah dan ditambah terakhir dengan peraturn daerah kotamadya mojokerto nomor 5 tahun 1972 sudah tidak sesuai lagi dengan keadaan dewasa ini maka di pandang perlu untuk mengubah pasal-pasal tersebut dengan peraturan daerah sebagai peraturan ketujuh kali', 'UU Nomor 5 Tahun 1974', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1978-02-18', '1978-04-12', '-', '-', 'PROKUM_DAERAH/Perda_No__16_th_1977.pdf', '', '', '', b'1', b'0', '2020-07-23 13:02:19', 1, '2020-07-23 13:02:19', 1);
