INSERT INTO `t_prokum` VALUES (1054, 1, 1, b'1', '5', 'PENJUALAN MINUMAN KERAS DAN PENGGUNAAN SERTA PEMUNGUTAN PAJAK ATAS PENJUALAN MINUMAN KERAS', 1986, 6, 'B', 'Perda No. 5 Tahun 1986', 'Ketentuan pemungutan Tarif pajak atas penjualan minuman keras sebagaimana di atur dalam peraturan daerah kotapraja mojokerto nomor 14 Tahun 1957 yang telah di ubah/ di tambah beberapa kali terakhir dengan peraturan  daerah kotamadya daerah tingkat II Mojokerto nomor 15 Tahun 1976.', '-', '-', 'Mengubah', '-', '1986-12-26', '1987-02-23', '1083/P', '1/A', 'PROKUM_DAERAH/Perda_No__5_th_1986.pdf', '', '', '', b'1', b'0', '2020-07-23 15:53:53', 1, '2020-07-23 15:53:53', 1);
INSERT INTO `t_prokum` VALUES (1055, 1, 1, b'1', '6', 'PELAYANAN KESEHATAN DAN RETRIBUSI DI RUMAH SAKIT UMUM DAN DAERAH DAN PUSAT KESEHATAN MASYARAKAT DAERAH KOTAMADYA DAERAH TINGKAT II MOJOKERTO', 1986, 23, 'C', 'Perda No. 6 Tahun 1986', 'Bahwa, Pelayanan kesehatan kepada masyarakat baik dirumah sakit umum daerah maupun dirumah sakit pusat kesehatan masyarakat perlu di tingkatkan sesuai dengan perkembangan dan kebutuhan masyarakat dalam rangka pemerataan kesejahteraan untuk menunjang suksesnya pembangunan di segala bidang.', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1986-04-26', '1986-07-01', '185/P', '5/B', 'PROKUM_DAERAH/Perda_No__6_th_1986.pdf', '', '', '', b'1', b'0', '2020-07-24 08:27:50', 1, '2020-07-24 08:27:50', 1);
INSERT INTO `t_prokum` VALUES (1056, 1, 1, b'1', '7', 'ANGGARAN PENDAPATAN DAN BELANJA DADERAH KOTAMADYA DAERAH TINGKAT II MOJOKERTO TAHUN ANGGARAN 1986/1987', 1986, 4, 'A', 'Perda No. 7 Tahun 1986', 'bahwa anggaran pendapatan dan belanja daerah kotamadya daerah tingkat II Mojokerto tahun anggaran 1986/1987 perlu di tetapkan dengan peraturan daerah.', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1986-04-28', '1986-05-20', '152/P', '3/C', 'PROKUM_DAERAH/Perda_No__7_th_1986.pdf', '', '', '', b'1', b'0', '2020-07-24 08:36:24', 1, NULL, NULL);
INSERT INTO `t_prokum` VALUES (1057, 1, 1, b'1', '8', 'PENETAPAN SEMENTARA SISA PERHITUNGAN ANGGARAN PENDAPATAN DAN BELANJA DAERAH KOTAMADYA DAERAH TINGKAT II MOJOKERTO TAHUN ANGGARAN 1985/1986', 1986, 4, 'A', 'Perda No. 8 Tahun 1986', 'Bahwa untuk memberikan landasan formil atas perhitungan anggaran pendapatan dan belanja daerah kotamadya daerah tingkat II Mojokerto Tahun anggaran 1985/1986 terhadap penyelenggaraan pemerintah kotamadya daerah tingkat II Mojokerto dalam tahun anggaran tersebut, maka dipandang perlu untuk menetapkan penetapan sementara sisa perhitungan anggaran pendapatan dan belanja daerah dalam peraturan daerah.', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1987-11-18', '1987-12-24', '290/P', '6/C', 'PROKUM_DAERAH/Perda_No__8_th_1986.pdf', '', '', '', b'1', b'0', '2020-07-24 08:43:05', 1, NULL, NULL);
INSERT INTO `t_prokum` VALUES (1058, 1, 1, b'1', '9', 'PERUBAHAN KETIGA PERATURAN DAERAH KOTAMADYA DAERAH TINGKAT II MOJOKERTO NOMOR 9 TAHUN 1977 TENTANG PENGADAAN DAN PENERANGAN UNTUK JALAN-JALAN UMUM', 1986, 5, 'C', 'Perda No. 9 Tahun 1986', 'Bahwa berhubung ketentuan retribusi penerangan untuk jalan-jalan umum sebagaimana di ataur dalam peratuuran daerah kotamadya daerah tingkat II Mojokerto nomor 1 tahun 1985', '-', '-', 'Berlaku', 'Berlaku sejak tanggal diundangkan', '1987-04-04', '1987-04-21', '95/P', '4/B', 'PROKUM_DAERAH/Perda_No__9_th_1986.pdf', '', '', '', b'1', b'0', '2020-07-24 08:57:31', 1, NULL, NULL);
INSERT INTO `t_prokum` VALUES (1059, 1, 1, b'1', '10', 'PERUBAHAN KEDUA PERATURAN DAERAH KOTAMADYA DAERAH TINGKAT II MOJOKERTO NOMOR 8 TAHUN 1978TENTANG KARTU TERNAK DAN JUAL BELI TERNAK.', 1986, 5, 'B', 'Perda No. 10 Tahun 1986', 'Bahwa ketentuan Besarnya pungutan Retribusi pemberian kartu ternak dan surat keterangan jual beli ternak sebaimana di atur dalam peraturan daerah kotamadya daerah tingkat II Mojokerto nomor 8 Tahun 1978 yang di ubah dengan peraturan daerah kotamadya daerah tingkat II mojokerto nomor 8 tahun 1982. sudah tidak sesuai lagi dengan perkembangan jaman atau keadaan saat ini, maka dipandandang perlu untuk diadakan perubahan dengan menuangkan ketentuan perubahan dimaksud dalam suatu peraturan daerah.', '-', '-', 'Mengubah', 'mengubah peraturan', '1987-12-21', '1988-01-16', '319/P', '1/B', 'PROKUM_DAERAH/Perda_No__10_th_1986.pdf', '', '', '', b'1', b'0', '2020-07-24 09:07:06', 1, NULL, NULL);
