-- ----------------------------
-- View structure for v_pengajuan
-- ----------------------------
DROP VIEW IF EXISTS `v_pengajuan`;
CREATE  VIEW `v_pengajuan` AS SELECT
	t_pengajuan.id, 
	t_pengajuan.jenis_pengajuan,
	(
    CASE 
        WHEN t_pengajuan.jenis_pengajuan = 1 THEN 'Peraturan Walikota (Perwali)'
        WHEN t_pengajuan.jenis_pengajuan = 2 THEN 'Keputusan Walikota (Kepwali)'
        WHEN t_pengajuan.jenis_pengajuan = 3 THEN 'Keputusan Sekretariat Daerah'
        WHEN t_pengajuan.jenis_pengajuan = 4 THEN 'Instruksi Walikota (Inwali)'
        WHEN t_pengajuan.jenis_pengajuan = 5 THEN 'Surat Edaran (SE)'
    END) AS jenis_pengajuan_text,
	t_pengajuan.judul, 
	t_pengajuan.nama, 
	t_pengajuan.jabatan, 
	t_pengajuan.nomor_hp, 
	t_pengajuan.jumlah_prokum, 
	(SELECT COUNT(t_penyusunan.id) FROM t_penyusunan WHERE id_pengajuan = t_pengajuan.id) AS jumlah_penyusunan,
	(SELECT COUNT(t_penyusunan.id) FROM t_penyusunan WHERE id_pengajuan = t_pengajuan.id AND (`status` = 1 OR is_ready)) AS jumlah_proses,
	(SELECT COUNT(t_penyusunan.id) FROM t_penyusunan WHERE id_pengajuan = t_pengajuan.id AND `status` = 2) AS jumlah_kembali,
	(SELECT COUNT(t_penyusunan.id) FROM t_penyusunan WHERE id_pengajuan = t_pengajuan.id AND is_ready) AS jumlah_siap,
	t_pengajuan.file_pengajuan_word, 
	t_pengajuan.file_pengajuan_excel, 
	t_pengajuan.file_pengajuan_tambahan, 
	t_pengajuan.keterangan, 
	t_pengajuan.`status`, 
	t_pengajuan.is_deleted, 
	t_pengajuan.created_date, 
	t_pengajuan.modified_by, 
	t_pengajuan.modified_date, 
	v_user_group.user_id, 
	v_user_group.user_username, 
	v_user_group.user_email, 
	v_user_group.user_active, 
	v_user_group.user_full_name, 
	v_user_group.user_phone, 
	v_user_group.group_name, 
	v_user_group.group_id, 
	v_user_group.group_description,
	( SELECT id FROM t_disposisi WHERE t_disposisi.id_pengajuan = t_pengajuan.id ) AS disposisi_id
FROM
	t_pengajuan
	LEFT JOIN
	v_user_group
	ON 
		t_pengajuan.created_by = v_user_group.user_id ;

