CREATE  VIEW `v_prokum_detail` AS select `t_prokum`.`id` AS `id`,`t_prokum`.`id_jenis` AS `id_jenis`,`t_prokum`.`id_kabinet` AS `id_kabinet`,`t_prokum`.`is_online` AS `is_online`,`t_prokum`.`no_peraturan` AS `no_peraturan`,`t_prokum`.`tentang` AS `tentang`,`t_prokum`.`tahun_peraturan` AS `tahun_peraturan`,`t_prokum`.`jumlah_halaman` AS `jumlah_halaman`,`t_prokum`.`seri_peraturan` AS `seri_peraturan`,`t_prokum`.`subjek_singkat` AS `subjek_singkat`,`t_prokum`.`dasar_pertimbangan` AS `dasar_pertimbangan`,`t_prokum`.`dasar_hukum` AS `dasar_hukum`,`t_prokum`.`catatan_penting` AS `catatan_penting`,`t_prokum`.`status_peraturan` AS `status_peraturan`,`t_prokum`.`detail_status_peraturan` AS `detail_status_peraturan`,`t_prokum`.`tanggal_penetapan` AS `tanggal_penetapan`,`t_prokum`.`tanggal_pengundangan` AS `tanggal_pengundangan`,`t_prokum`.`noreg_provinsi` AS `noreg_provinsi`,`t_prokum`.`noreg_daerah` AS `noreg_daerah`,`t_prokum`.`file` AS `file`,`t_prokum`.`pihak1` AS `pihak1`,`t_prokum`.`pihak2` AS `pihak2`,`t_prokum`.`masa_berlaku` AS `masa_berlaku`,`t_prokum`.`is_deleted` AS `is_deleted_prokum`,`t_prokum`.`created_date` AS `created_date`,`t_prokum`.`created_by` AS `created_by`,`t_prokum`.`modified_date` AS `modified_date`,`t_prokum`.`modified_by` AS `modified_by`,`t_prokum`.`is_upload` AS `is_upload`,`m_jenis_prokum`.`tipe` AS `tipe`,`m_jenis_prokum`.`singkatan` AS `singkatan`,`m_jenis_prokum`.`nama` AS `nama`,`m_jenis_prokum`.`is_deleted` AS `is_deleted_jenis`,`m_kabinet`.`macam` AS `macam`,`m_kabinet`.`keterangan` AS `keterangan`,`m_kabinet`.`posisi` AS `posisi`,`m_kabinet`.`is_deleted` AS `is_deleted_kabinet` from ((`t_prokum` join `m_jenis_prokum` on((`t_prokum`.`id_jenis` = `m_jenis_prokum`.`id`))) join `m_kabinet` on((`t_prokum`.`id_kabinet` = `m_kabinet`.`id`))); ;

